import React, { Component } from "react";

class APIs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            apis: []
        };
    }

    fetchRandomAPI = () => {
        fetch('https://api.publicapis.org/random')
            .then(res => res.json())
            .then(
                (data) => {
                    this.setState({
                        isLoaded: true,
                        apis: data.entries
                    });
                },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            );
    }

    componentDidMount() {
        this.fetchRandomAPI();
    }

    render() {
        const { error, isLoaded, apis } = this.state;
        return (
            <div>
                <button onClick={this.fetchRandomAPI}>Generate Random API</button>
                {error && <div>Error: {error.message}</div>}
                {!isLoaded ? (
                    <div>Loading...</div>
                ) : (
                    <div>
                        {apis.map((api) => (
                            <div key={api.API}>
                                <p>API: {api.API}</p>
                                <p>Description: {api.Description}</p>
                                <p>
                                    Link: <a href={api.Link} target="_blank" rel="noopener noreferrer">{api.Link}</a>
                                </p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        );
    }
}

export default APIs;
